contracts/echidna/Echidna_Donation.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.10;
import "./Helper.sol";
import {Donations} from "../Donations.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract Echidna_Donation is Helper, ERC721Holder {

    Donations donations;
    MockDAI dai;

    bytes32 TX = 0x5bdfd14fcc917abc2f02a30721d152a6f147f09e8cbaad4e0d5405d646c5c3e1;
    uint256 ID = 1;

    address[] owners = [carol, alice, bob, address(this)];

    constructor () {
        donations = new Donations(address(this));
        dai = new MockDAI(0);
    }

    // mint with valid params should never revert
    function mint_should_succeed(Donations.DonationParams[] memory _params) public {

        uint16 length = uint16(_params.length);
        require(length > 0);
        uint256 total = 0;
        for (uint16 i = 0; i < length; i++) {
            _params[i].destinationId = 1;
            _params[i].amount = Helper.one_to_max_uint64(_params[i].amount);
            total += _params[i].amount;
            _params[i].owner = owners[i % 4];
            _params[i].token = dai;
        }

        dai.mint(address(donations), total);
        emit Log("total", total);

        uint256 balance_donations_before = dai.balanceOf(address(donations));
        emit Log("balance of donations before", balance_donations_before);

        try donations.mint(TX, ID, _params) {
            assert(true);
        } catch {
            assert(false);
        }

        ID += 1;
    }
}

contracts/echidna/Echidna_Invalid_Deposi.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.10;
import "./Helper.sol";
import {IVault} from "../vault/IVault.sol";

contract Echidna_Invalid_Deposit is Helper {

    // deposit zero should always revert
    function deposit_with_zero_amount(IVault.DepositParams memory _params) public {
        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = 0;
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(10000, _params.claims);
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with any claim percentage zero should always revert
    function deposit_claim_pct_zero(IVault.DepositParams memory _params) public {
        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount);
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(10000, _params.claims);
        _params.claims[_params.amount % _params.claims.length].pct = 0;
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with invalid lockduration should always revert
    function deposit_invalid_lockduration_1(IVault.DepositParams memory _params) public {

        _params.lockDuration = (_params.lockDuration % (2 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount);
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(10000, _params.claims);
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with invalid lockduration should always revert
    function deposit_invalid_lockduration_2(IVault.DepositParams memory _params) public {
        _params.lockDuration = 1 + 24 weeks + _params.lockDuration;
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount);
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(10000, _params.claims);
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with claims not totalling exactly 100 percent should always revert
    function deposit_claims_more_than_100(IVault.DepositParams memory _params) public {

        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount); 
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(10000 + (uint16(_params.amount) % (type(uint16).max - 10000)), _params.claims);
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with claims not totalling exactly 100 percent should always revert
    function deposit_claims_less_than_100(IVault.DepositParams memory _params) public {
        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount); 
        emit Log("amount", _params.amount);

        Helper.mint_helper(address(this), _params.amount);
        populate_claims(uint16(_params.amount) % 9999, _params.claims);
        _params.inputToken = address(underlying);
        deposit_should_revert(_params);
    }

    // deposit with input token not swappable should always revert
    function deposit_swappable_no_pool(IVault.DepositParams memory _params) public {
        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        emit Log("lockDuration", _params.lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount);
        emit Log("amount", _params.amount);

        require(_params.inputToken != address(underlying));

        MockERC20(_params.inputToken).mint(address(this), _params.amount);
        MockERC20(_params.inputToken).approve(address(vault), _params.amount);
        populate_claims(10000, _params.claims);
        deposit_should_revert(_params);
    }
}

contracts/echidna/Echidna_Invalid_Withdr.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.10;
import "./Helper.sol";
import {IVault} from "../vault/IVault.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract Echidna_Invalid_Withdraw is Helper,ERC721Holder {

    uint256 amount;
    uint64 lockDuration;
    uint256[] depositIds = [0];
    uint256 time;
    bool init = false;

    // changes the deposit
    function change_deposit(IVault.DepositParams memory _params) public {

        if (init) {
            return;
        }

        _params.lockDuration = 2 weeks + (_params.lockDuration % (22 weeks));
        lockDuration = _params.lockDuration;
        emit Log("lockDuration", lockDuration);

        _params.amount = Helper.one_to_max_uint64(_params.amount);
        amount = _params.amount;
        emit Log("amount", amount);

        Helper.mint_helper(address(this), amount);

        populate_claims(10000, _params.claims);
        depositIds = vault.deposit(_params);

        time = block.timestamp;
        init = true;
    }

    // withdraw with less than was deposited should always succeed if
    // lockduration has passed, else always revert
    function withdraw_all() public {

        if (!init) {
            withdraw_should_revert(address(this), depositIds);
            return;
        }

        emit Log("amount", amount);
        emit Log("lockDuration", lockDuration);
        emit Log("time", time);

        uint256 balance_this_before = underlying.balanceOf(address(this));
        uint256 balance_vault_before = vault.totalUnderlying();

        emit Log("balance of this before", balance_this_before);
        emit Log("balance of vault before", balance_vault_before);

        uint256 totalshares_vault_before = vault.totalShares();
        emit Log("totalShares of vault before", totalshares_vault_before);

        uint256 totalprincipal_vault_before = vault.totalPrincipal();
        emit Log("totalPrincipal of vault before", totalprincipal_vault_before);

        if (block.timestamp > time + lockDuration) {
            withdraw_should_succeed(address(this), depositIds);
            init = false;

            uint256 balance_this_after = underlying.balanceOf(address(this));
            uint256 balance_vault_after = vault.totalUnderlying();

            emit Log("balance of this after", balance_this_after);
            emit Log("balance of vault after", balance_vault_after);

            assert(balance_vault_after == balance_vault_before - amount);
            assert(balance_this_after == balance_this_before + amount);

            emit Log("totalShares of vault after", vault.totalShares());
            assert(vault.totalShares() == totalshares_vault_before - (amount * (10**18)));

            emit Log("totalPrincipal of vault after", vault.totalPrincipal());
            assert(vault.totalPrincipal() == totalprincipal_vault_before - amount);
        } else {
            withdraw_should_revert(address(this), depositIds);
        }
    }

    function force_withdraw_all() public {

        if (!init) {
            withdraw_should_revert(address(this), depositIds);
            return;
        }

        emit Log("amount", amount);
        emit Log("lockDuration", lockDuration);
        emit Log("time", time);

        uint256 balance_this_before = underlying.balanceOf(address(this));
        uint256 balance_vault_before = vault.totalUnderlying();

        emit Log("balance of this before", balance_this_before);
        emit Log("balance of vault before", balance_vault_before);

        if (block.timestamp > time + lockDuration) {
            try vault.forceWithdraw(address(this), depositIds) {
                assert(true);
            } catch {
                assert(false);
            }

            init = false;

            uint256 balance_this_after = underlying.balanceOf(address(this));
            uint256 balance_vault_after = vault.totalUnderlying();

            emit Log("balance of this after", balance_this_after);
            emit Log("balance of vault after", balance_vault_after);

            assert(balance_vault_after == balance_vault_before - amount);
            assert(balance_this_after == balance_this_before + amount);
        } else {
            try vault.forceWithdraw(address(this), depositIds) {
                assert(false);
            } catch {
                assert(true);
            }
        }
    }
}

contracts/echidna/Echidna_LiquityStrateg.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import "./Helper.sol";

contract Echidna_LiquityStrategy is Helper {
    address constant SWAP_TARGET = 0xDef1C0ded9bec7F1a1670819833240f027b25EfF;

    // role allowed to invest/withdraw assets to/from the strategy (vault)
    bytes32 internal constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    // role allowed to call harvest() and reinvest()
    bytes32 internal constant KEEPER_ROLE = keccak256("KEEPER_ROLE");
    // role for managing swap targets whitelist
    bytes32 internal constant SETTINGS_ROLE = keccak256("SETTINGS_ROLE");

    // reverts if msg.sender is not manager
    function invest_not_manager() public {
	strategy.revokeRole(MANAGER_ROLE, address(this));
        try strategy.invest() {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // reverts if underlying balance is zero
    function invest_underlying_zero() public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	require(vault.totalUnderlying() == 0);
	strategy.grantRole(MANAGER_ROLE, address(this));
        try strategy.invest() {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // deposits underlying to the stabilityPool
    function invest_moves_balance_to_stabilitypool(uint256 _amount) public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	uint256 amount = one_to_max_uint64(_amount);
	underlying.mint(address(strategy), amount);
	strategy.grantRole(MANAGER_ROLE, address(this));
	uint256 before = underlying.balanceOf(address(stabilityPool));
        try strategy.invest() {
            assert(true);
        } catch {
            assert(false);
        }
	assert(underlying.balanceOf(address(strategy)) == 0);
	assert(underlying.balanceOf(address(stabilityPool)) == amount + before);
    }

    // reverts if msg.sender is not manager
    function withdrawToVault_not_manager() public {
	strategy.revokeRole(MANAGER_ROLE, address(this));
        try strategy.withdrawToVault(1) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // reverts if amount is zero
    function withdrawToVault_underlying_zero() public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	strategy.grantRole(MANAGER_ROLE, address(this));
        try strategy.withdrawToVault(0) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // reverts if amount is greater than invested assets
    function withdrawToVault_more_than_invested(uint256 _amount) public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	uint256 amount = strategy.investedAssets() + one_to_max_uint64(_amount);
	strategy.grantRole(MANAGER_ROLE, address(this));
        try strategy.withdrawToVault(amount) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // works if amount is less than invested assets
    function withdrawToVault_less_than_invested(uint256 _amount) public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	uint256 before_invested = strategy.investedAssets();
	emit Log("strategy.investedAssets()", strategy.investedAssets());
	require(before_invested != 0);
	uint256 amount = 1 + one_to_max_uint64(_amount) % before_invested;
	uint256 before_vault = underlying.balanceOf(address(vault));
	strategy.grantRole(MANAGER_ROLE, address(this));
        try strategy.withdrawToVault(amount) {
            assert(true);
        } catch {
            assert(false);
        }
	assert(strategy.investedAssets() == before_invested - amount);
	assert(underlying.balanceOf(address(vault)) == before_vault + amount);
    }

    // works if amount is equal than invested assets
    function withdrawToVault_equal_invested() public {
	require(address(this) == 0x5409ED021D9299bf6814279A6A1411A7e866A631);
	emit Log("strategy.investedAssets()", strategy.investedAssets());
	require(strategy.investedAssets() != 0);
	uint256 amount = strategy.investedAssets();
	uint256 before_vault = underlying.balanceOf(address(vault));
	strategy.grantRole(MANAGER_ROLE, address(this));
        try strategy.withdrawToVault(amount) {
            assert(true);
        } catch {
            assert(false);
        }
	assert(strategy.investedAssets() == 0);
	assert(underlying.balanceOf(address(vault)) == before_vault + amount);
    }

    // reverts if msg.sender is not settings role
    function allowSwapTarget_not_settings(address _swapTarget) public {
	strategy.revokeRole(SETTINGS_ROLE, address(this));
        try strategy.allowSwapTarget(_swapTarget) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // reverts if msg.sender is not settings role
    function denySwapTarget_not_settings(address _swapTarget) public {
	strategy.revokeRole(SETTINGS_ROLE, address(this));
        try strategy.denySwapTarget(_swapTarget) {
            assert(false);
        } catch {
            assert(true);
        }
    }

    // reverts if msg.sender is not keeper role
    function reinvest_not_keeper(address _swapTarget, uint256 _lqtyAmount, bytes calldata _lqtySwapData, uint256 _ethAmount, bytes calldata _ethSwapData, uint256 _amountOutMin) public {
	strategy.revokeRole(KEEPER_ROLE, address(this));
        try strategy.reinvest(_swapTarget, _lqtyAmount, _lqtySwapData, _ethAmount, _ethSwapData, _amountOutMin) {
            assert(false);
        } catch {
            assert(true);
        }
    }
}

contracts/echidna/Echidna_Sponsor.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.10;
import "./Helper.sol";
import {IVault} from "../vault/IVault.sol";
import {ERC721Holder} from "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";

contract Echidna_Sponsor is Helper,ERC721Holder {

    // add sponsor with valid params should never revert
    function sponsor_should_succeed(uint256 _amount, uint256 _lockDuration) public {

        _lockDuration = 2 weeks + (_lockDuration % (22 weeks));
        emit Log("lockDuration", _lockDuration);

        _amount = Helper.one_to_max_uint64(_amount);
        emit Log("amount", _amount);

        Helper.mint_helper(address(this), _amount);

        uint256 balance_this_before = underlying.balanceOf(address(this));
        uint256 balance_vault_before = vault.totalSponsored();
        emit Log("balance of this before", balance_this_before);
        emit Log("total sponsored of vault before", balance_vault_before);

        uint256 totalshares_vault_before = vault.totalShares();
        emit Log("totalShares of vault before", totalshares_vault_before);

        uint256 totalprincipal_vault_before = vault.totalPrincipal();
        emit Log("totalPrincipal of vault before", totalprincipal_vault_before);

        uint256 totalunderlying_vault_before = vault.totalUnderlying();
        emit Log("totalUnderlying of vault before", totalunderlying_vault_before);

        try vault.sponsor(address(underlying), _amount, _lockDuration, 5) {
            assert(true);
        } catch {
            assert(false);
        }

        uint256 balance_this_after = underlying.balanceOf(address(this));
        uint256 balance_vault_after = vault.totalSponsored();

        emit Log("balance of this after", balance_this_after);
        emit Log("total sponsored of vault after", balance_vault_after);

        assert(balance_vault_after == balance_vault_before + _amount);
        assert(balance_this_after == balance_this_before - _amount);

        emit Log("totalShares of vault after", vault.totalShares());
        assert(vault.totalShares() == totalshares_vault_before);

        emit Log("totalPrincipal of vault after", vault.totalPrincipal());
        assert(vault.totalPrincipal() == totalprincipal_vault_before);

        emit Log("totalUnderlying of vault after", vault.totalUnderlying());
        assert(vault.totalUnderlying() == totalunderlying_vault_before + _amount);
    }

    // sponsor zero should always revert
    function sponsor_with_zero_amount(uint256 lockDuration) public {
        lockDuration = 2 weeks + (lockDuration % (22 weeks));
        emit Log("lockDuration", lockDuration);
        emit Log("amount", 0);
        sponsor_should_revert(0, lockDuration);
    }

    // sponsor with invalid lockduration should always revert
    function sponsor_invalid_lockduration_1(uint256 amount, uint256 lockDuration) public {

        lockDuration = (lockDuration % (2 weeks));
        emit Log("lockDuration", lockDuration);

        amount = Helper.one_to_max_uint64(amount);
        emit Log("amount", amount);

        Helper.mint_helper(address(this), amount);
        sponsor_should_revert(amount, lockDuration);
    }

    // sponsor with invalid lockduration should always revert
    function sponsor_invalid_lockduration_2(uint256 amount, uint256 lockDuration) public {
        lockDuration = 1 + 24 weeks + lockDuration;
        emit Log("lockDuration", lockDuration);

        amount = Helper.one_to_max_uint64(amount);
        emit Log("amount", amount);

        Helper.mint_helper(address(this), amount);
        sponsor_should_revert(amount, lockDuration);
    }

    function sponsor_should_revert(uint256 _amount, uint256 _lockDuration) internal {
        try vault.sponsor(address(underlying), _amount, _lockDuration, 5) {
            assert(false);
        } catch {
            assert(true);
        }
    }
}

