// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";

import {IRyskLiquidityPool} from "../../interfaces/rysk/IRyskLiquidityPool.sol";

// This contract aims to mimic the Rysk LiquidityPool contract as much as possible. For instance,
// the LiquidityPool has the 'epoch' (or 'depositReceipts', etc.) public field and to match that
// we have the same field implemented here.
//
// The mechanics for deposits/withdrawals used in the Rysk LiquidityPool are replicated by this contract
// and it should behave in the same way as the LiquidityPool in that sense.
//
// Because we want to interact with the LiquidityPool contract's fields in our strategy through an interface,
// and we cannot declare a field on an interface, we do not implement the IRyskLiquidityPool interface here.
// Instead, our strategy tests ensure that the API defined by IRyskLiquidityPool and the actual API of the LiquidityPool
// (MockRyskLiquidityPool) are indeed a match, implying that public fields from the LiquidityPool are accessible
// through methods with the same name described by the IRyskLiquidityPool interface.
contract MockRyskLiquidityPool is ERC20 {
    ERC20 immutable underlying;
    uint256 public depositEpoch;
    uint256 public pendingDeposits;
    uint256 public withdrawalEpoch;
    uint256 public underlyingDecimals;

    mapping(uint256 => uint256) public depositEpochPricePerShare;
    mapping(uint256 => uint256) public withdrawalEpochPricePerShare;
    mapping(address => IRyskLiquidityPool.DepositReceipt)
        public depositReceipts;
    mapping(address => IRyskLiquidityPool.WithdrawalReceipt)
        public withdrawalReceipts;

    constructor(
        string memory _name,
        string memory _symbol,
        ERC20 _underlying
    ) ERC20(_name, _symbol) {
        underlying = _underlying;
        underlyingDecimals = _underlying.decimals();

        depositEpochPricePerShare[depositEpoch] = 1e18;
        depositEpoch++;
        withdrawalEpochPricePerShare[withdrawalEpoch] = 1e18;
        withdrawalEpoch++;
    }

    function deposit(uint256 _amount) external returns (bool) {
        require(_amount > 0);

        IRyskLiquidityPool.DepositReceipt
            storage depositReceipt = depositReceipts[msg.sender];

        if (depositReceipt.epoch != 0 && depositReceipt.epoch < depositEpoch) {
            depositReceipt.unredeemedShares += _getSharesForAmount(
                depositReceipt.amount,
                depositEpochPricePerShare[depositReceipt.epoch]
            );
        }

        if (depositReceipt.epoch == depositEpoch) {
            depositReceipt.amount += uint128(_amount);
        } else {
            depositReceipt.amount = uint128(_amount);
            depositReceipt.epoch = uint128(depositEpoch);
        }

        pendingDeposits += _amount;
        underlying.transferFrom(msg.sender, address(this), _amount);

        return true;
    }

    // note two different behaviors depending on epoch:
    // 1. when called second time and epoch has not advanced => add to the previous receipt
    // 2. when called second time and epoch has advanced => revert
    function initiateWithdraw(uint256 _shares) external {
        require(_shares > 0);

        redeem(type(uint256).max);

        require(
            _shares <= balanceOf(msg.sender),
            "Not enough shares to withdraw"
        );

        IRyskLiquidityPool.WithdrawalReceipt
            storage withdrawalReceipt = withdrawalReceipts[msg.sender];

        // transfer shares to the pool to initiate withdrawal
        _transfer(msg.sender, address(this), _shares);

        if (withdrawalReceipt.epoch == withdrawalEpoch) {
            // add to the previous receipt since withdrawal is already initiated for this epoch
            withdrawalReceipt.shares = uint128(
                withdrawalReceipt.shares + _shares
            );

            return;
        }

        if (withdrawalReceipt.shares > 0) {
            require(false, "Withdrawal already initiated");
        }

        withdrawalReceipt.epoch = uint128(withdrawalEpoch);
        withdrawalReceipt.shares = uint128(_shares);
    }

    function redeem(uint256 _sharesToRedeem) public returns (uint256) {
        require(_sharesToRedeem > 0, "Shares to redeem must be greater than 0");

        IRyskLiquidityPool.DepositReceipt
            storage depositReceipt = depositReceipts[msg.sender];

        if (
            depositReceipt.epoch == 0 ||
            (depositReceipt.amount == 0 && depositReceipt.unredeemedShares == 0)
        ) {
            return 0;
        }

        if (depositReceipt.epoch < depositEpoch && depositReceipt.amount != 0) {
            depositReceipt.unredeemedShares += _getSharesForAmount(
                depositReceipt.amount,
                depositEpochPricePerShare[depositReceipt.epoch]
            );

            depositReceipt.amount = 0;
        }

        if (depositReceipt.unredeemedShares == 0) {
            return 0;
        }

        _sharesToRedeem = _sharesToRedeem > depositReceipt.unredeemedShares
            ? depositReceipt.unredeemedShares
            : _sharesToRedeem;

        _transfer(address(this), msg.sender, _sharesToRedeem);

        depositReceipts[msg.sender].unredeemedShares -= _sharesToRedeem;

        return _sharesToRedeem;
    }

    function completeWithdraw() external returns (uint256) {
        IRyskLiquidityPool.WithdrawalReceipt
            storage withdrawalReceipt = withdrawalReceipts[msg.sender];

        _burn(address(this), withdrawalReceipt.shares);

        uint256 amount = _getAmountForShares(
            withdrawalReceipt.shares,
            withdrawalEpochPricePerShare[withdrawalReceipt.epoch]
        );
        underlying.transfer(msg.sender, amount);

        withdrawalReceipt.shares = 0;

        return amount;
    }

    function executeEpochCalculation() external {
        uint256 newPricePerShare = _calculateNewPricePerShare();

        _mintSharesForPendingDeposits(newPricePerShare);

        depositEpochPricePerShare[depositEpoch] = newPricePerShare;
        depositEpoch++;

        withdrawalEpochPricePerShare[withdrawalEpoch] = newPricePerShare;
        withdrawalEpoch++;
    }

    function executeOnlyDepositEpochCalculation() external {
        uint256 newPricePerShare = _calculateNewPricePerShare();
        _mintSharesForPendingDeposits(newPricePerShare);

        depositEpochPricePerShare[depositEpoch] = newPricePerShare;
        depositEpoch++;
    }

    function totalAssets() internal view returns (uint256) {
        return underlying.balanceOf(address(this));
    }

    function _getSharesForAmount(uint256 _amount, uint256 _pricePerShare)
        internal
        view
        returns (uint256)
    {
        return
            (((_amount * 1e18) / _pricePerShare) * 1e18) /
            10**underlyingDecimals;
    }

    function _getAmountForShares(uint256 _shares, uint256 _pricePerShare)
        internal
        view
        returns (uint256)
    {
        return
            (((_shares * _pricePerShare) / 1e18) * 10**underlyingDecimals) /
            1e18;
    }

    function _calculateNewPricePerShare() internal view returns (uint256) {
        if (totalSupply() == 0) {
            return 1e18;
        }

        return
            ((((totalAssets() - pendingDeposits) * 1e18) / totalSupply()) *
                1e18) / 10**underlyingDecimals;
    }

    function _mintSharesForPendingDeposits(uint256 _pricePerShare) internal {
        if (pendingDeposits == 0) return;

        uint256 sharesToMint = _getSharesForAmount(
            pendingDeposits,
            _pricePerShare
        );

        _mint(address(this), sharesToMint);
        delete pendingDeposits;
    }
}

contracts/mock/yearn/MockYearnVault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ERC20} from "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import {MockERC20} from "./../MockERC20.sol";

import {IYearnVault} from "../../interfaces/yearn/IYearnVault.sol";

contract MockYearnVault is IYearnVault {
    using SafeERC20 for ERC20;

    ERC20 immutable underlying;
    MockERC20 immutable vaultShares;

    uint256 public spyForMaxLossWithdrawParam;

    constructor(
        string memory _name,
        string memory _symbol,
        address _underlying
    ) {
        underlying = ERC20(_underlying);
        vaultShares = new MockERC20(_name, _symbol, underlying.decimals(), 0);
    }

    function deposit(uint256 amount, address recipient)
        public
        returns (uint256)
    {
        require(amount > 0);

        uint256 newAmount = amount;

        if (amount == type(uint256).max)
            newAmount = underlying.balanceOf(msg.sender);

        underlying.safeTransferFrom(msg.sender, address(this), newAmount);

        return _issueSharesForAmount(recipient, newAmount);
    }

    function pricePerShare() public view returns (uint256) {
        uint256 totalSupply = vaultShares.totalSupply();
        if (totalSupply == 0) return 10**underlying.decimals();
        return
            (10**underlying.decimals() * _getUnderlyingBalance()) / totalSupply;
    }

    function withdraw(
        uint256 maxShares,
        address recipient,
        uint256 _maxLoss
    ) public returns (uint256) {
        require(maxShares > 0);

        // spy on _maxLoss param
        spyForMaxLossWithdrawParam = _maxLoss;

        uint256 value = (maxShares * pricePerShare()) /
            10**underlying.decimals();

        vaultShares.burn(msg.sender, maxShares);

        underlying.transfer(recipient, value);

        return value;
    }

    function totalAssets() external view returns (uint256) {
        return underlying.balanceOf(address(this));
    }

    ///////////////// INTERNAL FUNCTIONS //////////////////////////////

    function _issueSharesForAmount(address to, uint256 amount)
        internal
        returns (uint256)
    {
        uint256 shares;
        uint256 totalSupply = vaultShares.totalSupply();
        if (totalSupply > 0) {
            shares = (amount * totalSupply) / _getUnderlyingBalance(); // dev: no free funds
        } else {
            shares = amount;
        }

        // Mint new shares
        vaultShares.mint(to, shares);

        return shares;
    }

    function _getUnderlyingBalance() internal view returns (uint256) {
        return underlying.balanceOf(address(this));
    }

    function balanceOf(address account)
        external
        view
        override
        returns (uint256)
    {
        return vaultShares.balanceOf(account);
    }

    function decimals() external view returns (uint256) {
        return vaultShares.decimals();
    }
}

contracts/strategy/BaseStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import {PercentMath} from "../lib/PercentMath.sol";
import {ERC165Query} from "../lib/ERC165Query.sol";
import {CustomErrors} from "../interfaces/CustomErrors.sol";
import {IStrategy} from "../strategy/IStrategy.sol";
import {IYearnVault} from "../interfaces/yearn/IYearnVault.sol";
import {IVault} from "../vault/IVault.sol";

/**
 * BaseStrategy provides common functionality for all strategies.
 */
abstract contract BaseStrategy is IStrategy, AccessControl, CustomErrors {
    using ERC165Query for address;

    /// role allowed to call invest and withdrawToVault
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    /// role allowed to change settings of the strategy
    bytes32 public constant SETTINGS_ROLE = keccak256("SETTINGS_ROLE");
    // role allowed to call maintenance functions
    bytes32 public constant KEEPER_ROLE = keccak256("KEEPER_ROLE");
    // underlying ERC20 token
    IERC20 public underlying;
    // the vault linked to this strategy
    address public vault;

    /**
     * @param _vault address of the vault that will use this strategy
     * @param _underlying address of the underlying token
     * @param _admin address of the administrator account
     */
    constructor(
        address _vault,
        IERC20 _underlying,
        address _admin
    ) {
        if (_admin == address(0)) revert StrategyAdminCannotBe0Address();
        if (address(_underlying) == address(0))
            revert StrategyUnderlyingCannotBe0Address();
        if (!_vault.doesContractImplementInterface(type(IVault).interfaceId))
            revert StrategyNotIVault();

        vault = _vault;
        underlying = IERC20(_underlying);

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(MANAGER_ROLE, _vault);
    }

    //
    // Modifiers
    //

    modifier onlyManager() {
        if (!hasRole(MANAGER_ROLE, msg.sender))
            revert StrategyCallerNotManager();
        _;
    }

    modifier onlySettings() {
        if (!hasRole(SETTINGS_ROLE, msg.sender))
            revert StrategyCallerNotSettings();
        _;
    }

    modifier onlyKeeper() {
        if (!hasRole(KEEPER_ROLE, msg.sender)) revert StrategyCallerNotKeeper();
        _;
    }

    modifier onlyAdmin() {
        if (!hasRole(DEFAULT_ADMIN_ROLE, msg.sender))
            revert StrategyCallerNotAdmin();
        _;
    }

    /**
     * Transfers administrator rights for the Strategy to another account,
     * revoking current admin roles and setting up the roles for the new admin.
     *
     * @notice Can only be called by the account with the ADMIN role.
     *
     * @param _newAdmin The new ADMIN account.
     */
    function transferAdminRights(address _newAdmin) external virtual onlyAdmin {
        _doTransferAdminRights(_newAdmin);
    }

    /**
     * Changes the account with the ADMIN role with the provided account address.
     *
     * @notice This is ment to be called only from the #transferAdminRights function of the implemeting contracts.
     *
     * @param _newAdmin The new admin account for the strategy.
     */
    function _doTransferAdminRights(address _newAdmin) internal {
        if (_newAdmin == address(0)) revert StrategyAdminCannotBe0Address();
        if (_newAdmin == msg.sender)
            revert StrategyCannotTransferAdminRightsToSelf();

        _grantRole(DEFAULT_ADMIN_ROLE, _newAdmin);

        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
    }

    /// @inheritdoc IStrategy
    function transferYield(address, uint256)
        external
        virtual
        override(IStrategy)
        returns (uint256)
    {
        return 0;
    }
}

contracts/strategy/IStrategy.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity =0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * IStrategy defines the interface for pluggable contracts used by vaults to invest funds and generate yield.
 *
 * @notice It's up to the strategy to decide what do to with investable assets provided by a vault.
 *
 * @notice It's up to the vault to decide how much to invest/disinvest from the total pool.
 */
interface IStrategy {
    /**
     * Emmited when funds are invested by the strategy.
     *
     *@param amount amount invested
     */
    event StrategyInvested(uint256 amount);
    /**
     * Emmited when funds are withdrawn (disinvested) by the strategy.
     *
     *@param amount amount withdrawn
     */
    event StrategyWithdrawn(uint256 amount);

    /**
     * Provides information about wether the strategy is synchronous or asynchronous.
     *
     * @notice Synchronous strategies support instant withdrawals,
     * while asynchronous strategies impose a delay before withdrawals can be made.
     *
     * @return true if the strategy is synchronous, false otherwise
     */
    function isSync() external view returns (bool);

    /**
     * The vault linked to this strategy.
     *
     * @return The vault's address
     */
    function vault() external view returns (address);

    /**
     * Withdraws the specified amount back to the vault (disinvests)
     *
     * @param amount Amount to withdraw
     *
     * @return actual amount withdrawn
     */
    function withdrawToVault(uint256 amount) external returns (uint256);

    /**
     * Transfers the @param _amount to @param _to in the more appropriate currency.
     *
     * For instance, for Liquity Yield DCA, the most appropriate currency may
     * be ETH since yield will be kept in ETH.
     *
     * @param _to address that will receive the funds.
     * @param _amount amount to transfer.
     *
     * @return amountTransferred amount in underlying equivalent to amount transferred in other currency.
     */
    function transferYield(address _to, uint256 _amount)
        external
        returns (uint256 amountTransferred);

    /**
     * Amount of the underlying currency currently invested by the strategy.
     *
     * @notice both held and invested amounts are included here, using the
     * latest known exchange rates to the underlying currency
     *
     * @return The total amount of underlying
     */
    function investedAssets() external view returns (uint256);

    /**
     * Indicates if assets are invested into strategy or not.
     *
     * @notice this will be used when removing the strategy from the vault
     * @return true if assets invested, false if nothing invested.
     */
    function hasAssets() external view returns (bool);

    /**
     * Deposits of all the available underlying into the yield generating protocol.
     */
    function invest() external;
}

contracts/strategy/liquity/LiquityDCAStr.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {PercentMath} from "../../lib/PercentMath.sol";
import {LiquityStrategy} from "./LiquityStrategy.sol";

/***
 * An extended version of LiquityStrategy that allows generated yield to be distributed as ETH.
 */
contract LiquityDCAStrategy is LiquityStrategy {
    using PercentMath for uint256;

    error StrategyETHTransferFailed(address to);
    error StrategySwapDataEmpty();
    error StrategyLQTYtoETHSwapFailed();

    event StrategyYieldTransferred(address to, uint256 amount);

    /// @inheritdoc LiquityStrategy
    function transferYield(address _to, uint256 _amount)
        external
        override(LiquityStrategy)
        onlyManager
        returns (uint256)
    {
        uint256 ethBalance = address(this).balance;

        if (ethBalance == 0) return 0;

        uint256 amountInETH = _getExchangeAmountInETH(_amount);

        uint256 ethToTransfer = amountInETH > ethBalance
            ? ethBalance
            : amountInETH;

        _sendETH(_to, ethToTransfer);

        uint256 equivalentAmountInUnderlying = (_amount * ethToTransfer) /
            amountInETH;

        emit StrategyYieldTransferred(_to, equivalentAmountInUnderlying);

        return equivalentAmountInUnderlying;
    }

    /**
     * Swaps LQTY tokens held by the strategy to ETH.
     *
     * @notice Swap data is real-time data obtained from '0x' api.
     *
     * @param _swapTarget the address of the '0x' contract performing the swap.
     * @param _amount the amount of LQTY tokens to swap. Has to match with the amount used to obtain @param _lqtySwapData from '0x' api.
     * @param _lqtySwapData data from '0x' api used to perform LQTY -> ETH swap.
     * @param _ethAmountOutMin minimum amount of ETH to receive for the swap.
     */
    function swapLQTYtoETH(
        address _swapTarget,
        uint256 _amount,
        bytes calldata _lqtySwapData,
        uint256 _ethAmountOutMin
    ) external onlyKeeper {
        _checkSwapTarget(_swapTarget);
        if (_amount == 0) revert StrategyAmountZero();
        if (_lqtySwapData.length == 0) revert StrategySwapDataEmpty();

        uint256 lqtyBalance = lqty.balanceOf(address(this));
        if (_amount > lqtyBalance) revert StrategyNotEnoughLQTY();

        lqty.approve(_swapTarget, _amount);

        uint256 ethBalance = address(this).balance;

        (bool success, ) = _swapTarget.call{value: 0}(_lqtySwapData);
        if (!success) revert StrategyLQTYtoETHSwapFailed();

        if (address(this).balance < ethBalance + _ethAmountOutMin)
            revert StrategyInsufficientOutputAmount();
    }

    /**
     * Gets the amount of ETH that can be exchanged for the given amount of underlying asset (LUSD).
     * Uses curve LUSD/USDT & ETH/USDT pools to calculate the amount of ETH that can be exchanged for the given amount of LUSD.
     *
     * @param _underlyingAmount The amount of underlying asset (LUSD) to be exchanged.
     */
    function _getExchangeAmountInETH(uint256 _underlyingAmount)
        internal
        view
        returns (uint256)
    {
        uint256 amountInUSDT = curveExchange.get_exchange_amount(
            LUSD_CURVE_POOL,
            address(underlying),
            USDT,
            _underlyingAmount
        );

        uint256 amountInETH = curveExchange.get_exchange_amount(
            WETH_CURVE_POOL,
            USDT,
            WETH,
            amountInUSDT
        );

        return amountInETH;
    }

    /**
     * Sends ETH to the specified @param _to address. Reverts if the transfer fails.
     *
     * @param _to The address to send ETH to
     * @param _amount The amount of ETH to send
     */
    function _sendETH(address _to, uint256 _amount) internal {
        (bool sent, ) = _to.call{value: _amount}("");

        if (!sent) revert StrategyETHTransferFailed(_to);
    }
}

contracts/strategy/liquity/LiquityStrate.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {AccessControlUpgradeable} from "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import {UUPSUpgradeable} from "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";

import {PercentMath} from "../../lib/PercentMath.sol";
import {IStrategy} from "../IStrategy.sol";
import {CustomErrors} from "../../interfaces/CustomErrors.sol";
import {IVault} from "../../vault/IVault.sol";
import {IVaultSponsoring} from "../../vault/IVaultSponsoring.sol";
import {IStabilityPool} from "../../interfaces/liquity/IStabilityPool.sol";
import {ERC165Query} from "../../lib/ERC165Query.sol";
import {ICurveExchange} from "../../interfaces/curve/ICurveExchange.sol";

/***
 * Liquity Strategy generates yield by investing LUSD assets into Liquity Stability Pool contract.
 * Stability pool gives out LQTY & ETH as rewards for liquidity providers.
 * The LQTY rewards are normal yield rewards
 * But the Stability Pool achievs ETH rewards by Liquidating Troves using the LUSD we deposited.
 * So our balance of LUSD goes down and we get an 1.1x (or higher) value of ETH. In short, we make a 10% profit in ETH everytime our LUSD is used for liquidation by the stability pool
 * the harvest method here withdraws those LQTY & ETH rewards, swaps them into LUSD and then deposits them back to the stability pool.
 * we should make sure to harvest at regular intervals because if the value of ETH rewards goes below 1x of the LUSD used for liquidation then we will make a net loss on our LUSD.
 * the contract uses 0xapi for swapping the tokens.
 */
contract LiquityStrategy is
    IStrategy,
    UUPSUpgradeable,
    AccessControlUpgradeable,
    CustomErrors
{
    using PercentMath for uint256;
    using ERC165Query for address;

    error StrategyETHSwapFailed();
    error StrategyLQTYSwapFailed();
    error StrategyNotEnoughETH();
    error StrategyNotEnoughLQTY();
    error StrategyNothingToReinvest();
    error StrategyStabilityPoolCannotBe0Address();
    error StrategyCurveExchangeCannotBe0Address();
    error StrategySwapTargetCannotBe0Address();
    error StrategySwapTargetNotAllowed();
    error StrategyInsufficientOutputAmount();
    error StrategyYieldTokenCannotBe0Address();
    error StrategyMinimumPrincipalProtection();

    event StrategyReinvested(uint256 amountInLUSD);

    address public constant WETH_CURVE_POOL =
        0xD51a44d3FaE010294C616388b506AcdA1bfAAE46;
    address public constant LUSD_CURVE_POOL =
        0xEd279fDD11cA84bEef15AF5D39BB4d4bEE23F0cA;
    address public constant CURVE_ROUTER =
        0x81C46fECa27B31F3ADC2b91eE4be9717d1cd3DD7;
    address public constant WETH = 0xC02aaA39b223FE8D0A0e5C4F27eAD9083C756Cc2;
    address public constant USDT = 0xdAC17F958D2ee523a2206206994597C13D831ec7;

    // role allowed to invest/withdraw assets to/from the strategy (vault)
    bytes32 public constant MANAGER_ROLE = keccak256("MANAGER_ROLE");
    // role allowed to call harvest() and reinvest()
    bytes32 public constant KEEPER_ROLE = keccak256("KEEPER_ROLE");
    // role for managing swap targets whitelist
    bytes32 public constant SETTINGS_ROLE = keccak256("SETTINGS_ROLE");

    IERC20 public underlying; // LUSD token
    /// @inheritdoc IStrategy
    address public override(IStrategy) vault;
    IStabilityPool public stabilityPool;
    IERC20 public lqty; // reward token
    mapping(address => bool) public allowedSwapTargets; // whitelist of swap targets
    ICurveExchange public curveExchange;

    /**
     * A percentage that specifies the minimum amount of principal to protect plus the sponsored amount.
     * This value acts as a threshold and is applied only when the total underlying assets are grater tha the minimum amount of principal to protect.
     * The protected principal is kept in LUSD.
     *
     * For instance, if the minimum protected principal is 150%, the total
     * principal is 100 LUSD, and the total yield is 100 LUSD. When the backend
     * rebalances the strategy, it has to ensure that at least 50 ETH+LQTY is
     * converted to LUSD to maintain a 150% minimum protected principal.
     */
    uint16 public minPrincipalProtectionPct;

    //
    // Modifiers
    //

    modifier onlyManager() {
        if (!hasRole(MANAGER_ROLE, msg.sender))
            revert StrategyCallerNotManager();
        _;
    }

    modifier onlyKeeper() {
        if (!hasRole(KEEPER_ROLE, msg.sender)) revert StrategyCallerNotKeeper();
        _;
    }

    modifier onlySettings() {
        if (!hasRole(SETTINGS_ROLE, msg.sender))
            revert StrategyCallerNotSettings();
        _;
    }

    modifier onlyAdmin() {
        if (!hasRole(DEFAULT_ADMIN_ROLE, msg.sender))
            revert StrategyCallerNotAdmin();
        _;
    }

    //
    // Initialize method (constructor alternative for proxy contracts)
    //

    function initialize(
        address _vault,
        address _admin,
        address _stabilityPool,
        address _lqty,
        address _underlying,
        address _keeper,
        uint16 _principalProtectionPct,
        address _curveExchange
    ) public initializer {
        __AccessControl_init();
        __UUPSUpgradeable_init();

        if (_admin == address(0)) revert StrategyAdminCannotBe0Address();
        if (_lqty == address(0)) revert StrategyYieldTokenCannotBe0Address();
        if (_stabilityPool == address(0))
            revert StrategyStabilityPoolCannotBe0Address();
        if (_underlying == address(0))
            revert StrategyUnderlyingCannotBe0Address();
        if (!_vault.doesContractImplementInterface(type(IVault).interfaceId))
            revert StrategyNotIVault();
        if (_keeper == address(0)) revert StrategyKeeperCannotBe0Address();
        if (_curveExchange == address(0))
            revert StrategyCurveExchangeCannotBe0Address();

        _grantRole(DEFAULT_ADMIN_ROLE, _admin);
        _grantRole(KEEPER_ROLE, _admin);
        _grantRole(SETTINGS_ROLE, _admin);
        _grantRole(MANAGER_ROLE, _vault);
        _grantRole(KEEPER_ROLE, _keeper);

        vault = _vault;
        underlying = IERC20(_underlying);
        stabilityPool = IStabilityPool(_stabilityPool);
        curveExchange = ICurveExchange(_curveExchange);
        lqty = IERC20(_lqty);
        minPrincipalProtectionPct = _principalProtectionPct;

        underlying.approve(_stabilityPool, type(uint256).max);
    }

    /**
     * Set the minimum principal protection percentage.
     *
     * @param _pct The new minimum principal protection percentage.
     */
    function setMinPrincipalProtectionPct(uint16 _pct) external onlySettings {
        minPrincipalProtectionPct = _pct;
    }

    /**
     * Transfers administrator rights for the Strategy to another account,
     * revoking current admin roles and setting up the roles for the new admin.
     *
     * @notice Can only be called by the account with the ADMIN role.
     *
     * @param _newAdmin The new Strategy admin account.
     */
    function transferAdminRights(address _newAdmin) external onlyAdmin {
        if (_newAdmin == address(0x0)) revert StrategyAdminCannotBe0Address();

        if (_newAdmin == msg.sender)
            revert StrategyCannotTransferAdminRightsToSelf();

        _grantRole(DEFAULT_ADMIN_ROLE, _newAdmin);
        _grantRole(KEEPER_ROLE, _newAdmin);
        _grantRole(SETTINGS_ROLE, _newAdmin);

        _revokeRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _revokeRole(KEEPER_ROLE, msg.sender);
        _revokeRole(SETTINGS_ROLE, msg.sender);
    }

    //
    // IStrategy
    //

    /// @inheritdoc IStrategy
    function isSync() external pure override(IStrategy) returns (bool) {
        return true;
    }

    /// @inheritdoc IStrategy
    function hasAssets()
        external
        view
        virtual
        override(IStrategy)
        returns (bool)
    {
        return investedAssets() != 0;
    }

    /// @inheritdoc IStrategy
    /// @notice LQTY rewards of the strategy waiting to be claimed in the liquity stability pool are not included
    /// @notice but the ETH rewards are included
    function investedAssets()
        public
        view
        virtual
        override(IStrategy)
        returns (uint256)
    {
        uint256 ethBalance = address(this).balance +
            stabilityPool.getDepositorETHGain(address(this));

        // need to do this because the get_exchange_amount method reverts if ethBalance is zero
        if (ethBalance == 0) {
            return stabilityPool.getCompoundedLUSDDeposit(address(this));
        }

        uint256 ethBalanceInUSDT = curveExchange.get_exchange_amount(
            WETH_CURVE_POOL,
            WETH,
            USDT,
            ethBalance
        );

        uint256 ethBalanceInLusd = curveExchange.get_exchange_amount(
            LUSD_CURVE_POOL,
            USDT,
            address(underlying),
            ethBalanceInUSDT
        );

        return
            stabilityPool.getCompoundedLUSDDeposit(address(this)) +
            ethBalanceInLusd;
    }

    /// @inheritdoc IStrategy
    /// @notice this will also claim any unclaimed gains in the stability pool
    function invest() external virtual override(IStrategy) onlyManager {
        uint256 balance = underlying.balanceOf(address(this));
        if (balance == 0) revert StrategyNoUnderlying();

        // claims LQTY & ETH rewards if there are any
        stabilityPool.provideToSP(balance, address(0));

        emit StrategyInvested(balance);
    }

    /// @inheritdoc IStrategy
    /// @notice will also claim unclaimed LQTY & ETH gains
    /// @notice when amount > total deposited, all available funds will be withdrawn
    function withdrawToVault(uint256 amount)
        external
        virtual
        override(IStrategy)
        onlyManager
        returns (uint256)
    {
        if (amount == 0) revert StrategyAmountZero();
        if (amount > investedAssets()) revert StrategyNotEnoughShares();

        // withdraws underlying amount and claims LQTY & ETH rewards
        stabilityPool.withdrawFromSP(amount);

        // use balance instead of amount since amount could be greater than what was actually withdrawn
        uint256 balance = underlying.balanceOf(address(this));
        underlying.transfer(vault, balance);

        emit StrategyWithdrawn(balance);

        return balance;
    }

    /**
     * Allows an address to be used as a swap target by adding it on the whitelist.
     *
     * @notice Can only be called by the account with the SETTINGS role.
     * @notice Swap targets are addresses of 0x contracts used for swapping ETH and LQTY tokens held by the strategy.
     */
    function allowSwapTarget(address _swapTarget) external onlySettings {
        _checkSwapTargetForZeroAddress(_swapTarget);

        allowedSwapTargets[_swapTarget] = true;
    }

    /**
     * Denies an address to be used as a swap target by removing it from the whitelist.
     *
     * @notice Can only be called by the account with the SETTINGS role.
     */
    function denySwapTarget(address _swapTarget) external onlySettings {
        _checkSwapTargetForZeroAddress(_swapTarget);

        allowedSwapTargets[_swapTarget] = false;
    }

    /**
     * Collects the LQTY & ETH rewards from the stability pool.
     */
    function harvest() external virtual {
        // call to withdrawFromSP with 0 amount will only claim rewards
        stabilityPool.withdrawFromSP(0);
    }

    /**
     * Swaps LQTY tokens and ETH held by the strategy to LUSD,
     * and reinvests the swapped LUSD amount into the stability pool.
     *
     * @notice Can only be called by the account with the KEEPER role.
     * @notice Swap data arguments provided are real-time data obtained from '0x' api.
     *
     * @param _swapTarget the address of the '0x' contract performing the tokens swap.
     * @param _lqtySwapData data used to perform LQTY -> LUSD swap. Leave empty to skip this swap.
     * @param _ethAmount amount of ETH to swap to LUSD, has to match with the amount used to obtain @param _ethSwapData.
     * @param _ethSwapData data used to perform ETH -> LUSD swap. Leave empty to skip this swap.
     * @param _amountOutMin the minimum amount of LUSD to be received after the ETH & LQTY -> LUSD swap.
     */
    function reinvest(
        address _swapTarget,
        uint256 _lqtyAmount,
        bytes calldata _lqtySwapData,
        uint256 _ethAmount,
        bytes calldata _ethSwapData,
        uint256 _amountOutMin
    ) external virtual onlyKeeper {
        _checkSwapTarget(_swapTarget);
        _checkMinPrincipalProtectionRequirement(_amountOutMin);

        _swapLQTYtoLUSD(_swapTarget, _lqtyAmount, _lqtySwapData);
        _swapETHtoLUSD(_swapTarget, _ethAmount, _ethSwapData);

        // reinvest LUSD gains into the stability pool
        uint256 balance = underlying.balanceOf(address(this));
        if (balance == 0) {
            revert StrategyNothingToReinvest();
        }

        if (balance < _amountOutMin) {
            revert StrategyInsufficientOutputAmount();
        }

        emit StrategyReinvested(balance);

        stabilityPool.provideToSP(balance, address(0));
    }

    /// @inheritdoc IStrategy
    function transferYield(address, uint256)
        external
        virtual
        override(IStrategy)
        onlyManager
        returns (uint256)
    {
        return 0;
    }

    /**
     * Checks if the minimum principal protection requirement is met.
     *
     * @param _amountOutMin the minimum amount of LUSD to be received after the ETH & LQTY -> LUSD swap.
     */
    function _checkMinPrincipalProtectionRequirement(uint256 _amountOutMin)
        internal
        view
    {
        uint256 totalDeposited = IVault(vault).totalPrincipal() +
            IVaultSponsoring(vault).totalSponsored();

        // check if the amountOutMin is enough to protect the principal plus sponsored
        if (
            totalDeposited.pctOf(minPrincipalProtectionPct) <=
            totalDeposited + _amountOutMin
        ) return;

        // minimum principal protection does not apply when total underlying value is less than min protected principal plus sponsored amount
        if (
            IVault(vault).totalUnderlying() <
            totalDeposited.pctOf(minPrincipalProtectionPct)
        ) return;

        revert StrategyMinimumPrincipalProtection();
    }

    /**
     * Checks if the provided swap target is 0 address or is not allowed and reverts if any of these conditions is true.
     */
    function _checkSwapTarget(address _swapTarget) internal view {
        _checkSwapTargetForZeroAddress(_swapTarget);

        if (!allowedSwapTargets[_swapTarget])
            revert StrategySwapTargetNotAllowed();
    }

    /**
     * Checks if the provided swap target is 0 address and reverts if true.
     */
    function _checkSwapTargetForZeroAddress(address _swapTarget) internal pure {
        if (_swapTarget == address(0))
            revert StrategySwapTargetCannotBe0Address();
    }

    /**
     * Swaps LQTY tokens held by the strategy to LUSD.
     *
     * @notice Swap data is real-time data obtained from '0x' api.
     *
     * @param _swapTarget the address of the '0x' contract performing the swap.
     * @param _amount the amount of LQTY tokens to swap. Has to match with the amount used to obtain @param _lqtySwapData from '0x' api.
     * @param _lqtySwapData data from '0x' api used to perform LQTY -> LUSD swap.
     */
    function _swapLQTYtoLUSD(
        address _swapTarget,
        uint256 _amount,
        bytes calldata _lqtySwapData
    ) internal {
        // don't do cross-contract call if nothing to swap
        if (_amount == 0 || _lqtySwapData.length == 0) return;

        uint256 lqtyBalance = lqty.balanceOf(address(this));
        if (_amount > lqtyBalance) revert StrategyNotEnoughLQTY();

        lqty.approve(_swapTarget, _amount);

        // perform the swap
        (bool success, ) = _swapTarget.call{value: 0}(_lqtySwapData);
        if (!success) revert StrategyLQTYSwapFailed();
    }

    /**
     * Swaps ETH held by the strategy to LUSD.
     *
     * @notice Swap data is real-time data obtained from '0x' api.
     *
     * @param _swapTarget the address of the '0x' contract performing the swap.
     * @param _amount the amount of ETH to swap. Has to match with the amount used to obtain @param _ethSwapData from '0x' api.
     * @param _ethSwapData data from '0x' api to perform ETH -> LUSD swap.
     */
    function _swapETHtoLUSD(
        address _swapTarget,
        uint256 _amount,
        bytes calldata _ethSwapData
    ) internal {
        // don't do cross-contract call if nothing to swap
        if (_amount == 0 || _ethSwapData.length == 0) return;

        uint256 ethBalance = address(this).balance;
        if (_amount > ethBalance) revert StrategyNotEnoughETH();

        (bool success, ) = _swapTarget.call{value: _amount}(_ethSwapData);

        if (!success) revert StrategyETHSwapFailed();
    }

    function _authorizeUpgrade(address newImplementation)
        internal
        override
        onlyAdmin
    {}

    /**
     * Strategy has to be able to receive ETH as stability pool rewards.
     */
    receive() external payable {}
}

contracts/strategy/rysk/RyskStrategy.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {IERC20Metadata} from "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

import {PercentMath} from "../../lib/PercentMath.sol";
import {ERC165Query} from "../../lib/ERC165Query.sol";
import {IStrategy} from "../IStrategy.sol";
import {BaseStrategy} from "../BaseStrategy.sol";
import {IRyskLiquidityPool} from "../../interfaces/rysk/IRyskLiquidityPool.sol";
import {IVault} from "../../vault/IVault.sol";

/**
 * RyskStrategy generates yield by investing into a Rysk LiquidityPool,
 * that serves to provide liquidity for a dynamic hedging options AMM.
 *
 * @notice This strategy is asyncrhonous (doesn't support immediate withdrawals).
 *
 */
contract RyskStrategy is BaseStrategy {
    using SafeERC20 for IERC20;
    using PercentMath for uint256;

    // rysk liquidity pool that this strategy is interacting with
    IRyskLiquidityPool public immutable ryskLqPool;

    // number of decimal places of a Liquidity Pool share (ERC20)
    uint256 constant SHARES_CONVERSION_FACTOR = 1e18;
    uint256 immutable underlyingDecimals;

    /**
     * Emmited when a withdrawal has been initiated.
     *
     * @param amount to be withdrawn
     */
    event StrategyWithdrawalInitiated(uint256 amount);

    // rysk liquidity pool cannot be 0 address
    error RyskLiquidityPoolCannotBe0Address();
    // no withdrawal initiated
    error RyskNoWithdrawalInitiated();
    // cannot complete withdrawal in the same epoch
    error RyskCannotCompleteWithdrawalInSameEpoch();

    /**
     * @param _vault address of the vault that will use this strategy
     * @param _admin address of the administrator account for this strategy
     * @param _ryskLiquidityPool address of the rysk liquidity pool that this strategy is using
     * @param _underlying address of the underlying token
     */
    constructor(
        address _vault,
        address _admin,
        address _keeper,
        address _ryskLiquidityPool,
        IERC20 _underlying
    ) BaseStrategy(_vault, _underlying, _admin) {
        if (_ryskLiquidityPool == address(0))
            revert RyskLiquidityPoolCannotBe0Address();
        if (_keeper == address(0)) revert StrategyKeeperCannotBe0Address();

        ryskLqPool = IRyskLiquidityPool(_ryskLiquidityPool);

        underlying.safeIncreaseAllowance(_ryskLiquidityPool, type(uint256).max);
        underlyingDecimals = IERC20Metadata(address(_underlying)).decimals();

        _grantRole(KEEPER_ROLE, _keeper);
    }

    //
    // IStrategy
    //

    /// @inheritdoc IStrategy
    function isSync() external pure override(IStrategy) returns (bool) {
        return false;
    }

    /// @inheritdoc IStrategy
    function hasAssets()
        external
        view
        virtual
        override(IStrategy)
        returns (bool)
    {
        if (_getSharesBalance() != 0) return true;

        IRyskLiquidityPool.DepositReceipt
            memory depositReceipt = _getDepositReceipt();

        if (depositReceipt.amount != 0 || depositReceipt.unredeemedShares != 0)
            return true;

        IRyskLiquidityPool.WithdrawalReceipt
            memory withdrawalReceipt = _getWithdrawalReceipt();

        return withdrawalReceipt.shares != 0;
    }

    /// @inheritdoc IStrategy
    function investedAssets()
        public
        view
        virtual
        override(IStrategy)
        returns (uint256)
    {
        uint256 currentWithdrawalEpoch = ryskLqPool.withdrawalEpoch();
        // since withdrawal price per share is not updated until the end of the epoch,
        // we need to use the price per share from the previous epoch
        uint256 latestWithdrawalPricePerShare = ryskLqPool
            .withdrawalEpochPricePerShare(currentWithdrawalEpoch - 1);

        uint256 amountInDepositReceipt = _getAmountFromDepositReceipt();

        uint256 amountInPendingWithdrawal = _getAmountForPendingWithdrawal(
            currentWithdrawalEpoch,
            latestWithdrawalPricePerShare
        );

        uint256 amountInRedeemedShares = _sharesToUnderlying(
            _getSharesBalance(),
            latestWithdrawalPricePerShare
        );

        return
            amountInDepositReceipt +
            amountInPendingWithdrawal +
            amountInRedeemedShares;
    }

    /// @inheritdoc IStrategy
    function invest() external virtual override(IStrategy) onlyManager {
        uint256 balance = underlying.balanceOf(address(this));

        if (balance == 0) revert StrategyNoUnderlying();

        emit StrategyInvested(balance);
        ryskLqPool.deposit(balance);
    }

    /// @inheritdoc IStrategy
    function withdrawToVault(uint256 _amount)
        external
        virtual
        override(IStrategy)
        onlyManager
        returns (uint256)
    {
        if (_amount == 0) revert StrategyAmountZero();

        uint256 withdrawalEpoch = ryskLqPool.withdrawalEpoch();
        _checkAndCompletePendingWithdrawal(withdrawalEpoch);

        // since withdrawal price per share is not updated until the end of the epoch,
        // we need to use the price per share from the previous epoch
        uint256 withdrawalPricePerShare = ryskLqPool
            .withdrawalEpochPricePerShare(withdrawalEpoch - 1);

        uint256 sharesToWithdraw = _calculateSharesNeededToWithdraw(
            _amount,
            withdrawalPricePerShare
        );

        emit StrategyWithdrawalInitiated(_amount);
        ryskLqPool.initiateWithdraw(sharesToWithdraw);

        return 0;
    }

    /**
     * Completes the pending withdrawal initiated in an earlier epoch.
     *
     * @notice Expected to be called by the backend (keeper bot) once the Rysk liquidity pool enters a new withdrawal epoch.
     * The backend should track the 'WithdrawalEpochExecuted' event on the Rysk LiquidityPool contract,
     * and act by calling this function when the event is emitted to complete pending withdrawal.
     */
    function completeWithdrawal() external onlyKeeper {
        IRyskLiquidityPool.WithdrawalReceipt
            memory withdrawalReceipt = _getWithdrawalReceipt();

        if (withdrawalReceipt.epoch == 0) revert RyskNoWithdrawalInitiated();
        if (withdrawalReceipt.epoch == ryskLqPool.withdrawalEpoch())
            revert RyskCannotCompleteWithdrawalInSameEpoch();

        _completePendingWithdrawal();
    }

    /**
     * Calculates the amount of shares for provided amount of underlying and price per share.
     *
     * @param _underlying amount of underlying
     * @param _pricePerShare price per share
     *
     * @return number of shares
     */
    function _underlyingToShares(uint256 _underlying, uint256 _pricePerShare)
        internal
        view
        returns (uint256)
    {
        return ((((_underlying * SHARES_CONVERSION_FACTOR) / _pricePerShare) *
            SHARES_CONVERSION_FACTOR) / 10**underlyingDecimals);
    }

    /**
     * Calculates the amount of underlying for provided number of Rysk liquidity pool shares and price per share.
     *
     * @param _shares number of shares in e18 decimals
     * @param _pricePerShare price per share i e18 decimals
     *
     * @return amount of underlying
     */
    function _sharesToUnderlying(uint256 _shares, uint256 _pricePerShare)
        internal
        view
        returns (uint256)
    {
        return
            (((_shares * 10**underlyingDecimals) / SHARES_CONVERSION_FACTOR) *
                _pricePerShare) / SHARES_CONVERSION_FACTOR;
    }

    /**
     * Checks if there exists a pending withdrawal from an earlier epoch and completes it.
     * Rysk liquidity pool doesn't allow to initiate another withdrawal if a pending withdrawal from older epoch was not completed.
     *
     * @param _currentWithdrawalEpoch withdrawal epoch
     */
    function _checkAndCompletePendingWithdrawal(uint256 _currentWithdrawalEpoch)
        internal
    {
        IRyskLiquidityPool.WithdrawalReceipt
            memory withdrawalReceipt = _getWithdrawalReceipt();

        if (
            withdrawalReceipt.epoch != 0 &&
            withdrawalReceipt.epoch != _currentWithdrawalEpoch &&
            withdrawalReceipt.shares > 0
        ) {
            _completePendingWithdrawal();
        }
    }

    /**
     * Completes the pending withdrawal.
     */
    function _completePendingWithdrawal() internal {
        uint256 amountWithdrawn = ryskLqPool.completeWithdraw();

        emit StrategyWithdrawn(amountWithdrawn);
        underlying.safeTransfer(vault, amountWithdrawn);
    }

    /**
     * Gets the amount of shares needed to withdraw the specified amount of underlying.
     *
     * @notice reverts if the amount of shares needed is greater than the amount of shares available
     *
     * @param _amount amount of underlying to withdraw
     * @param _pricePerShare price per share
     *
     * @return shares needed to withdraw the specified amount of underlying
     */
    function _calculateSharesNeededToWithdraw(
        uint256 _amount,
        uint256 _pricePerShare
    ) internal returns (uint256) {
        uint256 sharesBalance = _getSharesBalance();
        uint256 sharesNeeded = _underlyingToShares(_amount, _pricePerShare);

        if (sharesNeeded > sharesBalance) {
            // try to redeem any unredeemed shares
            uint256 redeemedShares = ryskLqPool.redeem(type(uint256).max);

            if (sharesBalance + redeemedShares < sharesNeeded)
                revert StrategyNotEnoughShares();
        }

        return sharesNeeded;
    }

    /**
     * Gets the withdrawal receipt for the strategy.
     *
     * @return withdrawal receipt
     */
    function _getWithdrawalReceipt()
        internal
        view
        returns (IRyskLiquidityPool.WithdrawalReceipt memory)
    {
        return ryskLqPool.withdrawalReceipts(address(this));
    }

    /**
     * Gets the deposit receipt for the strategy.
     *
     * @return deposit receipt
     */
    function _getDepositReceipt()
        internal
        view
        returns (IRyskLiquidityPool.DepositReceipt memory)
    {
        return ryskLqPool.depositReceipts(address(this));
    }

    /**
     * Gets the amount of shares owned by the strategy.
     *
     * @return amount of shares
     */
    function _getSharesBalance() internal view returns (uint256) {
        return ryskLqPool.balanceOf(address(this));
    }

    /**
     * Gets the amount of underlying from the deposit receipt which includes the pending deposit amount and unredeemed shares.
     * @notice the deposit receipt is updated only when user interacts with the Rysk liquidity pool contract (deposit, redeem, withdraw).
     *
     * @return amount of underlying form the deposit receipt
     */
    function _getAmountFromDepositReceipt() internal view returns (uint256) {
        IRyskLiquidityPool.DepositReceipt
            memory depositReceipt = _getDepositReceipt();
        uint256 depositEpoch = ryskLqPool.depositEpoch();

        if (
            depositReceipt.epoch == 0 ||
            (depositReceipt.amount == 0 && depositReceipt.unredeemedShares == 0)
        ) return 0;

        uint256 amountDepositedInCurrentEpoch = 0;
        uint256 pendingUnredeemedShares = 0;
        if (depositReceipt.epoch == depositEpoch) {
            // if epoch did not advance, shares for our deposit are not minted yet
            amountDepositedInCurrentEpoch = depositReceipt.amount;
        } else {
            // calulate the amount of shares that are minted but not yet added to the deposit receipt
            pendingUnredeemedShares = _underlyingToShares(
                depositReceipt.amount,
                ryskLqPool.depositEpochPricePerShare(depositReceipt.epoch)
            );
        }

        uint256 amountInUnredeemedShares = _sharesToUnderlying(
            depositReceipt.unredeemedShares + pendingUnredeemedShares,
            // use latest deposit epoch price per share
            ryskLqPool.depositEpochPricePerShare(depositEpoch - 1)
        );

        return amountDepositedInCurrentEpoch + amountInUnredeemedShares;
    }

    /**
     * Gets the amount of underlying for the pending withdrawal.
     *
     * @param _currentWithdrawalEpoch current withdrawal epoch
     * @param _latestWithdrawalPricePerShare latest withdrawal price per share
     *
     * @return amount of underlying for the pending withdrawal
     */
    function _getAmountForPendingWithdrawal(
        uint256 _currentWithdrawalEpoch,
        uint256 _latestWithdrawalPricePerShare
    ) internal view returns (uint256) {
        IRyskLiquidityPool.WithdrawalReceipt
            memory withdrawalReceipt = _getWithdrawalReceipt();

        if (withdrawalReceipt.epoch == 0) return 0;

        if (withdrawalReceipt.epoch == _currentWithdrawalEpoch)
            return
                _sharesToUnderlying(
                    withdrawalReceipt.shares,
                    _latestWithdrawalPricePerShare
                );

        return
            _sharesToUnderlying(
                withdrawalReceipt.shares,
                ryskLqPool.withdrawalEpochPricePerShare(withdrawalReceipt.epoch)
            );
    }
}

contracts/strategy/yearn/YearnStrategy.s.content:
// SPDX-License-Identifier: MIT
pragma solidity =0.8.10;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {AccessControl} from "@openzeppelin/contracts/access/AccessControl.sol";

import {PercentMath} from "../../lib/PercentMath.sol";
import {ERC165Query} from "../../lib/ERC165Query.sol";
import {IStrategy} from "../IStrategy.sol";
import {BaseStrategy} from "../BaseStrategy.sol";
import {CustomErrors} from "../../interfaces/CustomErrors.sol";
import {IYearnVault} from "../../interfaces/yearn/IYearnVault.sol";
import {IVault} from "../../vault/IVault.sol";

/**
 * YearnStrategy generates yield by investing into a Yearn vault.
 *
 * @notice This strategy is syncrhonous (supports immediate withdrawals).
 */
contract YearnStrategy is BaseStrategy {
    using SafeERC20 for IERC20;
    using PercentMath for uint256;
    using ERC165Query for address;

    /**
     * Emmited when the maxLossOnWithdraw (from Yearn vault) is changed.
     *
     * @param maxLoss new value for max loss withdraw param
     */
    event StrategyMaxLossOnWithdrawChanged(uint256 maxLoss);

    // yearn vault is 0x
    error StrategyYearnVaultCannotBe0Address();
    // max loss on withdraw from yearn > 100%
    error StrategyMaxLossOnWithdrawTooLarge();

    // yearn vault that this strategy is interacting with
    IYearnVault public immutable yVault;
    // multiplier for underlying convertion to shares
    uint128 public immutable conversionMultiplier;
    // used when withdrawing from yearn vault, 1 = 0.01%
    uint128 public maxLossOnWithdraw = 1;

    /**
     * @param _vault address of the vault that will use this strategy
     * @param _admin address of the administrator account for this strategy
     * @param _yVault address of the yearn vault that this strategy is using
     * @param _underlying address of the underlying token
     */
    constructor(
        address _vault,
        address _admin,
        address _yVault,
        IERC20 _underlying
    ) BaseStrategy(_vault, _underlying, _admin) {
        if (_yVault == address(0)) revert StrategyYearnVaultCannotBe0Address();

        yVault = IYearnVault(_yVault);
        conversionMultiplier = uint128(10**yVault.decimals());

        _grantRole(SETTINGS_ROLE, _admin);

        underlying.approve(_yVault, type(uint256).max);
    }

    /**
     * Transfers administrator rights for the Strategy to another account,
     * revoking current admin roles and setting up the roles for the new admin.
     *
     * @notice Can only be called by the account with the ADMIN role.
     *
     * @param _newAdmin The new Strategy admin account.
     */
    function transferAdminRights(address _newAdmin)
        external
        override(BaseStrategy)
        onlyAdmin
    {
        _doTransferAdminRights(_newAdmin);

        _grantRole(SETTINGS_ROLE, _newAdmin);

        _revokeRole(SETTINGS_ROLE, msg.sender);
    }

    /// @inheritdoc IStrategy
    function isSync() external pure override(IStrategy) returns (bool) {
        return true;
    }

    /// @inheritdoc IStrategy
    function hasAssets()
        external
        view
        virtual
        override(IStrategy)
        returns (bool)
    {
        return _getShares() != 0;
    }

    /// @inheritdoc IStrategy
    function investedAssets()
        external
        view
        virtual
        override(IStrategy)
        returns (uint256)
    {
        return _sharesToUnderlying(_getShares()) + _getUnderlyingBalance();
    }

    /// @inheritdoc IStrategy
    function invest() external virtual override(IStrategy) onlyManager {
        uint256 beforeBalance = _getUnderlyingBalance();
        if (beforeBalance == 0) revert StrategyNoUnderlying();

        yVault.deposit(type(uint256).max, address(this));

        uint256 afterBalance = _getUnderlyingBalance();

        emit StrategyInvested(beforeBalance - afterBalance);
    }

    /// @inheritdoc IStrategy
    function withdrawToVault(uint256 _amount)
        external
        virtual
        override(IStrategy)
        onlyManager
        returns (uint256)
    {
        if (_amount == 0) revert StrategyAmountZero();
        uint256 uninvestedUnderlying = _getUnderlyingBalance();

        if (_amount > uninvestedUnderlying) {
            uint256 sharesToWithdraw = _underlyingToShares(
                _amount - uninvestedUnderlying
            );

            if (sharesToWithdraw > _getShares())
                revert StrategyNotEnoughShares();

            // burn shares and withdraw required underlying to strategy
            uint256 withdrawnFromYearn = yVault.withdraw(
                sharesToWithdraw,
                address(this),
                maxLossOnWithdraw
            );

            _amount = uninvestedUnderlying + withdrawnFromYearn;
        }

        // transfer underlying to vault
        underlying.safeTransfer(vault, _amount);

        emit StrategyWithdrawn(_amount);

        return _amount;
    }

    /**
     * Sets the max loss percentage used when withdrawing from the Yearn vault.
     *
     * @notice Can only be called by the account with settings role.
     *
     * @param _maxLoss The max loss percentage to use when withdrawing from the Yearn vault. Value of 1 equals 0.01% loss.
     */
    function setMaxLossOnWithdraw(uint128 _maxLoss) external onlySettings {
        if (_maxLoss > 10000) revert StrategyMaxLossOnWithdrawTooLarge();

        maxLossOnWithdraw = _maxLoss;

        emit StrategyMaxLossOnWithdrawChanged(_maxLoss);
    }

    /**
     * Get the underlying balance of the strategy.
     *
     * @return underlying balance of the strategy
     */
    function _getUnderlyingBalance() internal view returns (uint256) {
        return underlying.balanceOf(address(this));
    }

    /**
     * Get the number of yearn vault shares owned by the strategy.
     *
     * @return shares owned by the strategy
     */
    function _getShares() internal view returns (uint256) {
        return yVault.balanceOf(address(this));
    }

    /**
     * Calculates the value of yearn vault shares in underlying.
     *
     * @param _shares number of yearn vault shares
     *
     * @return underlying value of yearn vault shares
     */
    function _sharesToUnderlying(uint256 _shares)
        internal
        view
        returns (uint256)
    {
        return (_shares * yVault.pricePerShare()) / conversionMultiplier;
    }

    /**
     * Calculates the amount of underlying in number of yearn vault shares.
     *
     * @param _underlying amount of underlying
     *
     * @return number of yearn vault shares
     */
    function _underlyingToShares(uint256 _underlying)
        internal
        view
        returns (uint256)
    {
        return (_underlying * conversionMultiplier) / yVault.pricePerShare();
    }
}

